import React, { useState } from 'react';
import sidBg from '../assets/sid.jpg';
import { Award, Users } from 'lucide-react';

// Embedded thumbnail version of IMG_20230909_143213_175.jpg
const EMBEDDED_PHOTO = "data:image/jpeg;base64,";

export default function ProfileHero({ photoUrl }) {

  // Prefer bundled asset to work in both dev and production builds
  const bgPhoto = sidBg;

  const [avatarError, setAvatarError] = useState(false);

  const containerStyle = {
    position: 'relative',
    marginBottom: 32
  };


  const panelStyle = {
    // gradient overlay + primary image + embedded fallback
    backgroundImage: `linear-gradient(90deg, rgba(230,246,255,0.6) 0%, rgba(230,255,247,0.6) 100%), url(${bgPhoto}), url(${EMBEDDED_PHOTO})`,
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    borderRadius: 20,
    padding: 50,
    boxShadow: 'inset 0 1px 0 rgba(0,0,0,0.02)'
  };

  const avatarWrap = {
    position: 'relative',
    width: 128,
    height: 128,
    margin: '0 auto',
    marginTop: -64,
    marginBottom: 16,
    zIndex: 4
  };

  const avatarRing = {
    position: 'absolute',
    inset: 0,
    borderRadius: '50%',
    background: '#fff',
    transform: 'translate(4px,4px)',
    boxShadow: '0 8px 20px rgba(0,0,0,0.12)'
  };

  const avatarInner = {
    position: 'absolute',
    top: 8,
    left: 8,
    right: 8,
    bottom: 8,
    borderRadius: '50%',
    background: 'linear-gradient(90deg,#1e90ff,#00c6a7)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff',
    fontWeight: 700,
    fontSize: 20,
    overflow: 'hidden'
  };

  const statLeft = {
    position: 'absolute',
    left: 0,
    bottom: -18,
    background: '#fff',
    borderRadius: 8,
    padding: '10px 14px',
    boxShadow: '0 10px 20px rgba(0,0,0,0.08)',
    zIndex: 2
  };

  const statRight = {
    position: 'absolute',
    right: 0,
    top: -12,
    background: '#fff',
    borderRadius: 8,
    padding: '8px 12px',
    boxShadow: '0 10px 20px rgba(0,0,0,0.08)',
    zIndex: 3
  };

  const statNumber = { fontSize: 20, fontWeight: 700 };
  const statLabel = { fontSize: 12, color: '#6b7280' };

  return (
    <section style={containerStyle}>
      <div style={panelStyle}>
        <div style={{ maxWidth: 900, margin: '0 auto', textAlign: 'center', position: 'relative' }}>
          <div style={avatarWrap}>
            <div style={avatarRing} aria-hidden="true" />
            <div style={avatarInner}>
              <img
                src={bgPhoto}
                alt="Dr. Siddharth Sakalle"
                style={{ width: '100%', height: '100%', objectFit: 'cover', borderRadius: '50%' }}
                onError={(e) => {
                  const el = e.currentTarget;
                  if (!el.dataset.fallback) {
                    el.dataset.fallback = '1';
                    el.src = '/assets/sid.jpg'; // fallback to public asset
                  } else {
                    el.src = EMBEDDED_PHOTO; // final inline fallback
                  }
                }}
              />
            </div>
          </div>

          <h1 style={{ fontSize: 28, fontWeight: 800, color: '#0f172a' }}>Dr. Siddharth Sakalle</h1>
          <div style={{ maxWidth: 720, margin: '12px auto 0', color: '#374151', lineHeight: 1.6, textAlign: 'center' }}>
            {/* Trusted by Olympic athletes and Sports champions. Specialized in manual therapy, musculoskeletal rehabilitation, and sports injury recovery with international expertise. */}
          </div>
        </div>
      </div>

      <div style={statLeft}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
          <Users color="#2563eb" size={20} />
          <div>
            <div style={statNumber}>500+</div>
            <div style={statLabel}>Clients Treated</div>
          </div>
        </div>
      </div>

      <div style={statRight}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
          <Award color="#059669" size={18} />
          <div>
            <div style={statNumber}>98%</div>
            <div style={statLabel}>Recovery Rate</div>
          </div>
        </div>
      </div>
    </section>
  );
}